// Main App Content Component
function AppContent() {
  const navigate = useNavigate();
  const location = useLocation();
  const [walletAddress, setWalletAddress] = useState('');

  useEffect(() => {
    // Check if wallet is already connected
    const storedAddress = localStorage.getItem('walletAddress');
    if (storedAddress && blockchainService.isConnected) {
      setWalletAddress(storedAddress);
    }
  }, []);

  const handleWalletConnect = (address) => {
    setWalletAddress(address);
  };

  const handleWalletDisconnect = () => {
    setWalletAddress('');
  };

  const isActivePath = (path) => {
    if (path === '/' && location.pathname === '/') return true;
    if (path !== '/' && location.pathname.startsWith(path)) return true;
    return false;
  };

  const handleFeatureClick = (feature) => {
    switch (feature) {
      case 'ai':
        navigate('/ai-analysis');
        break;
      case 'provenance':
        navigate('/provenance');
        break;
      case 'marketplace':
        navigate('/marketplace');
        break;
      case 'incentives':
        toast.info('Sustainable Incentives: Earn tokens for verified data and sustainable practices');
        // In a real app, this would show incentive dashboard
        break;
      default:
        break;
    }
  };

  return (
    <AppContainer>
      <Header>
        <Nav>
          <Logo onClick={() => navigate('/')} style={{ cursor: 'pointer' }}>
            <LogoIcon>🌾</LogoIcon>
            FarmMate
          </Logo>

          <NavLinks>
            <NavLink 
              href="#" 
              active={isActivePath('/')} 
              onClick={(e) => { e.preventDefault(); navigate('/'); }}
            >
              Home
            </NavLink>
            <NavLink 
              href="#" 
              active={isActivePath('/marketplace')} 
              onClick={(e) => { e.preventDefault(); navigate('/marketplace'); }}
            >
              Marketplace
            </NavLink>
            <NavLink 
              href="#" 
              active={isActivePath('/provenance')} 
              onClick={(e) => { e.preventDefault(); navigate('/provenance'); }}
            >
              Provenance
            </NavLink>
            <NavLink 
              href="#" 
              active={isActivePath('/ai-analysis')} 
              onClick={(e) => { e.preventDefault(); navigate('/ai-analysis'); }}
            >
              AI Analysis
            </NavLink>
          </NavLinks>

          <Wallet 
            onConnect={handleWalletConnect}
            onDisconnect={handleWalletDisconnect}
          />
        </Nav>

        <MobileMenu>
          <MobileNavLink 
            href="#" 
            active={isActivePath('/')} 
            onClick={(e) => { e.preventDefault(); navigate('/'); }}
          >
            Home
          </MobileNavLink>
          <MobileNavLink 
            href="#" 
            active={isActivePath('/marketplace')} 
            onClick={(e) => { e.preventDefault(); navigate('/marketplace'); }}
          >
            Marketplace
          </MobileNavLink>
          <MobileNavLink 
            href="#" 
            active={isActivePath('/provenance')} 
            onClick={(e) => { e.preventDefault(); navigate('/provenance'); }}
          >
            Provenance
          </MobileNavLink>
          <MobileNavLink 
            href="#" 
            active={isActivePath('/ai-analysis')} 
            onClick={(e) => { e.preventDefault(); navigate('/ai-analysis'); }}
          >
            AI Analysis
          </MobileNavLink>
        </MobileMenu>
      </Header>

      <Main>
        <Routes>
          <Route path="/" element={
            <WelcomeSection>
              <WelcomeTitle>Welcome to FarmMate</WelcomeTitle>
              <WelcomeSubtitle>
                The decentralized precision agriculture marketplace connecting farmers, 
                buyers, and AI-powered insights for sustainable farming.
              </WelcomeSubtitle>

              <FeatureGrid>
                <FeatureCard onClick={() => handleFeatureClick('ai')}>
                  <FeatureIcon>🤖</FeatureIcon>
                  <FeatureTitle>AI-Powered Analysis</FeatureTitle>
                  <FeatureDescription>
                    On-device disease detection and quality scoring using advanced machine learning models.
                  </FeatureDescription>
                </FeatureCard>

                <FeatureCard onClick={() => handleFeatureClick('provenance')}>
                  <FeatureIcon>⛓️</FeatureIcon>
                  <FeatureTitle>Blockchain Provenance</FeatureTitle>
                  <FeatureDescription>
                    Immutable supply chain tracking from farm to table with full transparency.
                  </FeatureDescription>
                </FeatureCard>

                <FeatureCard onClick={() => handleFeatureClick('marketplace')}>
                  <FeatureIcon>💰</FeatureIcon>
                  <FeatureTitle>Fair Trade Marketplace</FeatureTitle>
                  <FeatureDescription>
                    Direct farmer-to-buyer transactions with escrow protection and fair pricing.
                  </FeatureDescription>
                </FeatureCard>

                <FeatureCard onClick={() => handleFeatureClick('incentives')}>
                  <FeatureIcon>🌱</FeatureIcon>
                  <FeatureTitle>Sustainable Incentives</FeatureTitle>
                  <FeatureDescription>
                    Token rewards for verified data and sustainable farming practices.
                  </FeatureDescription>
                </FeatureCard>
              </FeatureGrid>
            </WelcomeSection>
          } />

          <Route path="/marketplace" element={<Marketplace />} />
          <Route path="/provenance" element={<Provenance />} />
          <Route path="/ai-analysis" element={<AIAnalysis />} />
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </Main>

      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="dark"
      />
    </AppContainer>
  );
}

